    <div>
        <DefaultLayout />
        {/* FOLD */}
        <div style={{
            backgroundColor: '#E0ECF7', 
            margin: '0 auto', 
            width: '80vw', 
            height: '40vw', 

        }}>
          <div style={{
            display: 'grid',
            gridTemplateColumns: '26.6vw 26.6vw 26.6vw',
            backgroundColor: '#E0ECF7',
            color: '#444'
          }}>
            {/* THIS IS A */}
            <div style={{
                  gridColumn: '1 / 3',
                  gridRow: '1',
                  minHeight: '200px',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center'
                }}>
                <h1 style={{marginRight: '14vw'}}>
                  Yum Tash Food Lovers Home
                </h1>
            </div>
            <div style={{
                  gridColumn: '3',
                  gridRow:' 1',
                  minHeight: '200px'
                }}>
              <div style={{
                 borderLeft: '1px solid black',
                 height: '50px'
              }}>
                This is B
              </div>

            </div>
            {/* LINE 1 */}
            <div
              style={{
                gridColumn: '1 / 3',
                gridRow:'2',
                minHeight: '0px'
              }}>
                <hr style={{ 
                    maxWidth: '400px', 
                    marginLeft: '35vw',
                    border: '0.2px solid black'
                  }} />
            </div>
            {/* LINE 2 */}
            <div
              style={{
                gridColumn: '3',
                gridRow:'2',
                minHeight: '0px'
              }}>
                <hr style={{ 
                    maxWidth: '400px', 
                    marginRight: '6.1vw',
                    border: '0.2px solid black'
                  }} />
            </div>
              {/* Card */}
            <div style={{
              gridColumn: '1 / 3',
              gridRow: '3',
              borderRight: '1px solid black',
              borderBottom: '1px solid black',
            }}>
              <div>




                <div style={{
                    marginBottom: '2vw',
                    marginLeft: '2vw', 
                    backgroundColor: '#1C060A',
                    marginTop: '2vw', 
                    maxWidth: '50vw',
                    minWidth: '50vw',
                    minHeight: '20vw',
                    maxHeight: '20vw',
                    // borderRadius: '10px'
                    }}
                >
                  <div style={{display: 'flex', justifyContent: 'space-around'}}>
                    <div>
                      <h3 style={{
                            marginLeft: '10px',
                            color: 'white'
                          }}
                        >
                          {posts[0].title}
                      </h3>

                      <img
                        style={{marginLeft: '10px', marginTop: '30px'}}
                        src={urlFor(posts[0].mainImage)
                          .width(200)
                          .url()}
                      />
                    </div>
                    {/* CARD */}
                    <div  style={{
                        color: 'white', 
                        marginTop: '1.5vw', 
                        backgroundColor: '#226666', 
                        padding: '5px 20px', 
                        minWidth: '24vw',
                        maxWidth: '28vw',
                        maxHeight: '320px',
                        borderRadius: '10px'
                        }}
                      >
                      <h2>
                        Description
                      </h2>
                      <p style={{color: 'white'}}>
                        Author: {posts[0].name}
                      </p>
                      <BlockContent
                        // style={{ maxWidth: '100px !important'}}
                        blocks={posts[0].introduction}
                        // imageOptions={{ w: 300, h: 100, fit: 'max' }}
                        {...client.config()}
                      />
                      {/* <p style={{color: 'white', fontSize: '12px'}}>
                          {introduction}
                      </p> */}
                    </div>
                  </div>
                </div>
            </div>
            </div>

          </div>
      </div>  
      {/* SECOND PART */}
      {/* <div style={{backgroundColor: '#E0ECF7', margin: '0 auto', width: '80%', height: '500px', display: 'flex', justifyContent: 'center'}}>
        <div>
          <h2 style={{marginRight: '400px'}}>
            SISTE NYTT
          </h2>

        </div>
        <img
            style={{maxHeight: '300px', marginLeft: '100px'}}
            src={dessert}
        />
      </div>   */}
    </div>

    )
}
