import Link from 'next/link'
import groq from 'groq'
import client from '../client'
import BlockContent from '@sanity/block-content-to-react'

import DefaultLayout from '../layouts';
import imageUrlBuilder from '@sanity/image-url'
import dessert from '../public/dessert.jpeg';

import Card from '../components/card/card.component'


function urlFor (source) {
  return imageUrlBuilder(client).image(source)
}

const Index = (props) => {
    const { 
      introduction = [],
      posts = [], 
      name,
      slug
    } = props
    return (
    <div>
        <DefaultLayout />
        {/* FOLD */}
        <div style={{
            // backgroundImage: 'linear-gradient(to right, rgb(0, 154, 164), rgb(21, 32, 33))',
            // backgroundColor: '#E0ECF7', 
            margin: '0 auto', 
            width: '100vw', 
            height: '40vw', 

        }}>
          <div style={{
            display: 'grid',
            gridTemplateColumns: '26.6vw 26.6vw 26.6vw',
            // backgroundColor: '#E0ECF7',
            // backgroundImage: 'linear-gradient(to right, rgb(198, 253, 234), rgb(242, 194, 188))',
            color: '#444'
          }}>
            {/* THIS IS A */}
            <div style={{
                  gridColumn: '1 / 3',
                  gridRow: '1',
                  minHeight: '200px',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  borderBottom: '1px solid #D9D9D9',
                  borderRight: '1px solid #D9D9D9'
                }}>
                {/* <h1 style={{
                  fontFamily: "Archer SSm A",
                  fontSize: '32px',
                  marginRight: '14vw'
                }}>

                </h1> */}
                <div>
                  <div style={{
                        marginBottom: '2vw',
                        marginLeft: '2vw', 
                        backgroundColor: 'black',
                        marginTop: '2vw', 
                        maxWidth: '50vw',
                        minWidth: '50vw',
                        minHeight: '20vw',
                        maxHeight: '20vw',
                        borderRadius: '10px'
                      }}
                  >

                  </div>
              </div>
            </div>
            {/* THIS IS B */}
            <div style={{
                  gridColumn: '3',
                  gridRow:' 1',
                  minHeight: '200px',
                  display: 'flex',
                  borderBottom: '1px solid #D9D9D9'
                }}>
              <h3
                style={{
                  marginTop: '4vw',
                  paddingTop: '16px',
                  fontFamily: "Archer SSm A",
                  fontSize: '17px',
                  textAlign: 'center',
                  paddingLeft: '15%',
                  paddingRight: '15%',
                  letterSpacing: '0.19em',
                  fontWeight: '900',
                  lineHeight: '1.6em'
                }}
              >
                I denne bloggen tar vi for oss retter som er 
                sesongbaserte, da vi tenker ekstra på miljøet.
              </h3>
            </div>
            {/* Card */}
              <div style={{
                gridColumn: '1 / 3',
                gridRow: '3',
              }}>

            </div>
          </div>
      </div>  
    </div>

    )
}

