import DefaultLayout from '../layouts';
import imageUrlBuilder from '@sanity/image-url'
import dessert from '../public/dessert.jpeg';

function urlFor (source) {
  return imageUrlBuilder(client).image(source)
}

const Index = (props) => {
    const { 
      posts = [], 
      // mainImage,
      // title = 'Missing title',
      // name = 'Missing name',
      introduction = ''
    } = props
    return (
    <div>
        <DefaultLayout>

        </DefaultLayout>
        <div style={{backgroundColor: '#E0ECF7', margin: '0 auto', width: '80%', height: '500px', display: 'flex', justifyContent: 'space-evenly'}}>
        <h1 style={{marginTop: '45px'}}>Yum Tash Food Lovers Home</h1>
          {/* <div>
            <h1 style={{marginTop: '45px'}}>Yum Tash Food Lovers Home</h1>
