import groq from 'groq'
import imageUrlBuilder from '@sanity/image-url'
import BlockContent from '@sanity/block-content-to-react'
import client from '../../client'

import styles from './slug.module.scss'

// Components
import Header from '../../components/menu/menu.component';



function urlFor (source) {
  return imageUrlBuilder(client).image(source)
}

const Post = (props) => {
  console.log(props)
  const {
    title = 'Missing title',
    name = 'Missing name',
    categories,
    authorImage,
    mainImage,
    secondBody,
    body = [],
    introduction = [],
    titleA,
    ingredientsA = [],
    titleB,
    ingredientsB = [],
    titleC,
    ingredientsC = [],
    titleD,
    ingredientsD = [],
    secondImage
  } = props
  return (
    <div>
      <Header />
      <article className={styles.body}>
        <div></div>
        <div className={styles.leftSide}>
          <h1 className={styles.title}>{title}</h1>
          <img
              className={styles.mainImage}
              src={urlFor(mainImage)
              .width(500)
              .url()}
          />



          <BlockContent
            className={styles.introduction}
            blocks={introduction}
            imageOptions={{ w: 320, h: 240, fit: 'max' }}
            {...client.config()}
          />
          <div className={styles.ingredients}>
              <div>
                <h3 className={styles.titles}>{titleA}</h3>
                <BlockContent
                  className={styles.ingredientsA}
                  blocks={ingredientsA}
                  imageOptions={{ w: 320, h: 240, fit: 'max' }}
                  {...client.config()}
                />
              </div>
              <div>
              <h3 className={styles.titles}>{titleB}</h3>
                <BlockContent
                  className={styles.ingredientsA}
                  blocks={ingredientsB}
                  imageOptions={{ w: 320, h: 240, fit: 'max' }}
                  {...client.config()}
                />
              </div>
              <div>
              <h3 className={styles.titles}>{titleC}</h3>
              <BlockContent
                  className={styles.ingredientsA}
                  blocks={ingredientsC}
                  imageOptions={{ w: 320, h: 240, fit: 'max' }}
                  {...client.config()}
                />
              </div>
              <div>
              <h3 className={styles.titles}>{titleD}</h3>
                <BlockContent
                    className={styles.ingredientsA}
                    blocks={ingredientsD}
                    imageOptions={{ w: 320, h: 240, fit: 'max' }}
                    {...client.config()}
                  />
              </div>
          </div>
          <BlockContent
            className={styles.paragraph}
            blocks={body}
            imageOptions={{ w: 320, h: 240, fit: 'max' }}
            {...client.config()}
          />

          <h2>SLIK GJÃ˜R DU</h2>
          <img
              className={styles.mainImage}
              src={urlFor(secondImage)
              .width(500)
              .url()}
          />    
          <h2>{title}</h2>
          <BlockContent
            className={styles.paragraph}
            blocks={secondBody}
            imageOptions={{ w: 320, h: 240, fit: 'max' }}
            {...client.config()}
          />
          <img
              className={styles.mainImage}
              src={urlFor(thirdImage)
              .width(500)
              .url()}
          /> 
          <BlockContent
            className={styles.paragraph}
            blocks={thirdBody}
            imageOptions={{ w: 320, h: 240, fit: 'max' }}
            {...client.config()}
          />
          <img
              className={styles.mainImage}
              src={urlFor(fourthImage)
              .width(500)
              .url()}
          /> 
          <BlockContent
            className={styles.paragraph}
            blocks={fourthBody}
            imageOptions={{ w: 320, h: 240, fit: 'max' }}
            {...client.config()}
          />
          <img
              className={styles.mainImage}
              src={urlFor(fifthImage)
              .width(500)
              .url()}
          />

        </div>

        {/* RIGHT SIDE */}
        <div>
            <h2>This is the right side</h2>
            {categories && (
            <ul>
              Stikkord / Hashtags
              {categories.map(category => <p key={category}>{category}</p>)}
            </ul>
          )}
           {authorImage && (
            <div>
              <img
                className={styles.profilePic}
                src={urlFor(authorImage)
                  .width(100)
                  .url()}
              />
            </div>
          )}
          <span>Av {name}</span>
        </div>

      </article>
    </div>

  )
}

const query = groq`*[_type == "post" && slug.current == $slug][0]{
  title,
  "name": author->name,
  "categories": categories[]->title,
  "authorImage": author->image,
  mainImage,
  secondImage,
  secondBody,
  introduction,
  titleA,
  ingredientsA,
  titleB,
  ingredientsB,
  titleC,
  ingredientsC,
  titleD,
  ingredientsD,
  body
}`

Post.getInitialProps = async function (context) {
  // It's important to default the slug so that it doesn't return "undefined"
  const { slug = "" } = context.query
  return await client.fetch(query, { slug })
}

export default Post
